"use strict";var dropdownContainerDOM=document.querySelector(".dropdown-container"),templateCategoryDropdownDOM=document.querySelector("#template-category-dropdown"),templateNewsItemDOM=document.querySelector("#template-news-item"),archivedNews=JSON.parse(sessionStorage.getItem("archived-news"));archivedNews.forEach(function(e){var t=templateCategoryDropdownDOM.content.cloneNode(!0),o=t.querySelector(".dropdown-content");t.querySelector(".dropdown-title").innerText=e.title;for(var n=0;n<e.items.length;n++){var r=templateNewsItemDOM.content.cloneNode(!0);r.querySelector(".news-item-headline").innerText=e.items[n].headline,r.querySelector(".news-item-body").innerText=e.items[n].body,r.querySelector(".news-item-link").href=e.items[n].link,r.querySelector(".news-item-icon").src=e.items[n].icon,o.appendChild(r)}dropdownContainerDOM.appendChild(t)});