"use strict";var categoryDropdownDOM=document.querySelectorAll(".category-dropdown");categoryDropdownDOM.forEach(function(e){e.addEventListener("click",function(e){for(var t=e.composedPath(),r=0;r<t.length;r++)if(t[r].classList.contains("category-dropdown")){var o=t[r].querySelector(".dropdown-arrow"),a=t[r].parentNode.querySelector(".dropdown-content");t[r].classList.contains("turned")?(t[r].classList.remove("turned"),anime({targets:o,rotate:"0deg",duration:500}),anime({targets:a,height:"0rem",duration:500,easing:"easeInOutExpo"})):(t[r].classList.add("turned"),anime({targets:o,rotate:"90deg",duration:500}),anime({targets:a,height:9*a.children.length+"rem",duration:500}));break}})});