"use strict";var togglesDOM=document.querySelectorAll(".option-checkbox");togglesDOM.forEach(function(e){"true"==sessionStorage.getItem(e.id)&&e.click(),e.addEventListener("change",function(e){sessionStorage.setItem(e.target.id,e.target.checked)})});var darkModeBtnDOM=document.querySelector(".dark-mode-button");darkModeBtnDOM.addEventListener("click",function(){document.body.classList.contains("dark-mode")?(document.body.classList.remove("dark-mode"),sessionStorage.setItem("dark-mode","false")):(document.body.classList.add("dark-mode"),sessionStorage.setItem("dark-mode","true"))});